svg.script:

function show(evt, node)
{
        var svgdoc = evt.target.ownerDocument;
        var obj = svgdoc.getElementById(node);
        obj.setAttribute("visibility", "visible");
}

function hide(evt, node)
{
        var svgdoc = evt.target.ownerDocument;
        var obj = svgdoc.getElementById(node);
        obj.setAttribute("visibility" , "hidden");
}
